import React, { useState } from 'react';
import './App.css';
import MaterialTable from 'material-table'
import { Button } from '@material-ui/core';
import AddIcon from "@material-ui/icons/Add";
import XLSX from 'xlsx';
import { uploadFile } from './services/UploadFile';

const EXTENSIONS = ['xlsx', 'xls', 'csv']
function App() {
  const [colDefs, setColDefs] = useState();
  const [data, setData] = useState();
  const [isDisabled, setIsDisabled] = useState(true);

  const getExention = (file) => {
    const parts = file.name.split('.')
    const extension = parts[parts.length - 1]
    return EXTENSIONS.includes(extension) // return boolean
  }

  const convertToJson = (headers, data) => {
    const rows = []
    data.forEach(row => {
      let rowData = {}
      row.forEach((element, index) => {
        rowData[headers[index]] = element
      })
      rows.push(rowData)

    });
    return rows
  }


  const importExcel = (e) => {
    const file = e.target.files[0]

    const reader = new FileReader()
    reader.onload = (event) => {
      //parse data

      const bstr = event.target.result
      const workBook = XLSX.read(bstr, { type: "binary" })

      //get first sheet
      const workSheetName = workBook.SheetNames[0]
      const workSheet = workBook.Sheets[workSheetName]

      //getSecondSheet
      const workSheetNameTwo = workBook.SheetNames[1]
      const workSheetTwo = workBook.Sheets[workSheetNameTwo]

      //convert to array
      const fileData = XLSX.utils.sheet_to_json(workSheet, { header: 1 })
      const fileDataTwo = XLSX.utils.sheet_to_json(workSheetTwo, { header: 1 })

      // console.log(fileData)
      const headers = fileData[0]
      const headersTwo = fileDataTwo[0]
      const heads = headers.map(head => ({ title: head, field: head }))

      console.log(heads);

      setColDefs(heads)

      //removing header
      fileData.splice(0, 1)

      setData(convertToJson(headersTwo, fileData))
      setIsDisabled(false)
    }

    if (file) {
      if (getExention(file)) {
        reader.readAsBinaryString(file)
      }
      else {
        alert("Invalid file input, Select Excel, CSV file")
      }
    } else {
      setData([])
      setColDefs([])
    }
  }

  function loadData() {
    console.log('llegue');
    alert('llegue');
    uploadFile(data);
  }


  return (
    <div className="App">
      <h1 align="center">Opticar</h1>
      <h4 align='center'>Importe los datos desde un Excel</h4>
      <div className='ButtonContainer'>
        <div className="Buttons">
        <Button variant="contained" component="label" color="primary">
          {" "}
          <AddIcon /> Subir archivo
          <input type="file" onChange={importExcel} hidden />
        </Button>
        </div>
        <div className="Buttons">
        <Button variant="contained" disabled={isDisabled} onClick={loadData}>
          {" "}
          Cargar los datos
        </Button>
        </div>
      </div>
      <MaterialTable title="Datos de pedidos" data={data} columns={colDefs} />
    </div>
  );
}

export default App;
